package Lec1;

public class ThreeNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 8;
		int b = 72;
		int c = 34;
		if (a > b && a > c) {
			System.out.println(a);
		} else if (b > a && b > c) {
			System.out.println(b);
		}

		else {
			System.out.println(c);
		}

	}

}
