package Lec7;

public class Function_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 9;
		System.out.println(x);
		Addition();

	}

	public static void Addition() {

		int a = 9;
		int b = 7;
		int c = a + b;
		sub();
		System.out.println(c);
	}

	public static void sub() {

		int a = 9;
		int b = 7;
		int c = a - b;
		System.out.println(c);
	}

}
